import React from 'react';
import Card from '../Card/Card';

class WeekContainer extends React.Component {
  state = {
    days: [],
    city: "Klagenfurt",
    weatherURL: `https://api.openweathermap.org/data/2.5/forecast?q=Klagenfurt&lang=en&units=metric&APPID=79cce12f32339641751ec2b2c1ccfc2f`
  }


  componentDidMount = (i) => {
    fetch(i ? i : this.state.weatherURL)
    .then(res => res.json())

    .then(data => {
      const dailyData = data.list.filter(reading => reading.dt_txt.includes("12:00:00"))
      this.setState({days: dailyData})
    })
  }

  formatCards = () => {

    return this.state.days.map((day, index) => <Card day={day} key={index}/>)
  }
  inputstate = (e) => {
    let weatherURL  = `https://api.openweathermap.org/data/2.5/forecast?q=${e.target.value}&lang=en&units=metric&APPID=79cce12f32339641751ec2b2c1ccfc2f`

    this.setState({...this.state,city: e.target.value,
      weatherURL: weatherURL
    })

    this.componentDidMount(weatherURL)
    console.log(this.componentDidMount(weatherURL))


  }


  render() {
    return (
      <div className="">

        {}
      </div>
    )
  }
}

export default WeekContainer
