import React from 'react';
import Card from '../Card/Card';

class WeekContainer extends React.Component {
  state = {
    days: [],
    city: "Moskau",
    weatherURL: `https://api.openweathermap.org/data/2.5/forecast?q=Kiev&lang=en&units=metric&APPID=79cce12f32339641751ec2b2c1ccfc2f`
  }


  upDateLink = () => {

  }

  componentDidMount = () => {
    fetch(this.state.weatherURL)
    .then(res => res.json())

    .then(data => {
      const dailyData = data.list.filter(reading => reading.dt_txt.includes("12:00:00"))
      this.setState({days: dailyData})
    })
  }

  formatCards = () => {

    return this.state.days.map((day, index) => <Card day={day} key={index}/>)
  }
  inputstate = (e) => {
    this.setCity(e.target.value)
    console.log(this.city)
  }

  render() {
    return (
      <div className="">
        <h1>{this.state.city}</h1>
      <h1 className="display-4 jumbotron">Weather forecast for 5 days</h1>
        <input onInput={this.inputstate} type="text"/>
        <div className="row justify-content-center">

          {this.formatCards()}

        </div>
      </div>
    )
  }
}

export default WeekContainer
